mapper.select:
SELECT A.*,B.str_nm,B.str_img FROM tb_brd AS A LEFT JOIN tb_str AS B ON A.str_sn = B.str_sn WHERE A.safe_yn= #{safe_yn}
AND( A.brd_st = #{searchWrd} OR A.brd_ctgr LIKE CONCAT ('%', #{searchWrd},'%') OR B.str_nm LIKE CONCAT ('%', #{searchWrd},'%') OR A.brd_adr LIKE CONCAT ('%', #{searchWrd},'%') )
AND A.brd_st = #{searchWrd}
AND A.brd_ctgr LIKE CONCAT ('%', #{searchWrd},'%')
AND B.str_nm LIKE CONCAT ('%', #{searchWrd},'%')
AND A.brd_adr LIKE CONCAT ('%', #{searchWrd},'%')
ORDER BY A.rgtr_dt DESC;
